import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Dashboard from './components/Dashboard';
import './App.css';

// Set axios base URL
axios.defaults.baseURL = 'http://localhost:5000';

// Add axios interceptor to include token in requests
axios.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

interface FormData {
  employee_id: string;
  role: string;
  username: string;
  password: string;
  name: string;
  nic: string;
  gender: string;
  date_of_birth: string;
  branch_id: string;
  contact_id: string;
}

interface LoginData {
  username: string;
  password: string;
  role: string;
}

function LoginRegister() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState<FormData>({
    employee_id: '',
    role: 'Agent',
    username: '',
    password: '',
    name: '',
    nic: '',
    gender: 'Male',
    date_of_birth: '',
    branch_id: '',
    contact_id: ''
  });

  const [loginData, setLoginData] = useState<LoginData>({
    username: '',
    password: '',
    role: 'Agent'
  });

  const navigate = useNavigate();

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/register', formData);
      alert('Registration successful!');
      setFormData({
        employee_id: '',
        role: 'Agent',
        username: '',
        password: '',
        name: '',
        nic: '',
        gender: 'Male',
        date_of_birth: '',
        branch_id: '',
        contact_id: ''
      });
      // Switch to login tab after successful registration
      setIsLogin(true);
    } catch (error: any) {
      alert(error.response?.data?.message || 'Registration failed');
    }
  };

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/login', loginData);
      const { token, user } = response.data;
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));
      alert(`Login successful as ${user.role}`);
      // Redirect to dashboard
      navigate('/dashboard');
    } catch (error: any) {
      alert(error.response?.data?.message || 'Login failed');
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleLoginChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setLoginData({
      ...loginData,
      [name]: value
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Microbanking System</h1>
        <div className="auth-container">
          <div className="tabs">
            <button 
              className={isLogin ? 'active' : ''} 
              onClick={() => setIsLogin(true)}
            >
              Login
            </button>
            <button 
              className={!isLogin ? 'active' : ''} 
              onClick={() => setIsLogin(false)}
            >
              Register
            </button>
          </div>

          {isLogin ? (
            <form className="auth-form" onSubmit={handleLogin}>
              <h2>Login</h2>
              <div className="form-group">
                <label>Role:</label>
                <select 
                  name="role" 
                  value={loginData.role} 
                  onChange={handleLoginChange}
                  required
                >
                  <option value="Admin">Admin</option>
                  <option value="Manager">Manager</option>
                  <option value="Agent">Agent</option>
                </select>
              </div>
              <div className="form-group">
                <label>Username:</label>
                <input
                  type="text"
                  name="username"
                  value={loginData.username}
                  onChange={handleLoginChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Password:</label>
                <input
                  type="password"
                  name="password"
                  value={loginData.password}
                  onChange={handleLoginChange}
                  required
                />
              </div>
              <button type="submit">Login</button>
            </form>
          ) : (
            <form className="auth-form" onSubmit={handleRegister}>
              <h2>Employee Registration</h2>
              <div className="form-group">
                <label>Employee ID:</label>
                <input
                  type="text"
                  name="employee_id"
                  value={formData.employee_id}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Role:</label>
                <select 
                  name="role" 
                  value={formData.role} 
                  onChange={handleInputChange}
                  required
                >
                  <option value="Agent">Agent</option>
                  <option value="Manager">Manager</option>
                </select>
              </div>
              <div className="form-group">
                <label>Username:</label>
                <input
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Password:</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Full Name:</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>NIC:</label>
                <input
                  type="text"
                  name="nic"
                  value={formData.nic}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Gender:</label>
                <select 
                  name="gender" 
                  value={formData.gender} 
                  onChange={handleInputChange}
                  required
                >
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="form-group">
                <label>Date of Birth:</label>
                <input
                  type="date"
                  name="date_of_birth"
                  value={formData.date_of_birth}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Branch ID:</label>
                <input
                  type="text"
                  name="branch_id"
                  value={formData.branch_id}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter branch ID (e.g., BR001)"
                />
              </div>
              <div className="form-group">
                <label>Contact ID:</label>
                <input
                  type="text"
                  name="contact_id"
                  value={formData.contact_id}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter contact ID (e.g., CT001)"
                />
              </div>
              <button type="submit">Register</button>
            </form>
          )}
        </div>
      </header>
    </div>
  );
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route 
          path="/" 
          element={isAuthenticated ? <Navigate to="/dashboard" /> : <LoginRegister />} 
        />
        <Route 
          path="/dashboard" 
          element={isAuthenticated ? <Dashboard /> : <Navigate to="/" />} 
        />
      </Routes>
    </Router>
  );
}

export default App;
