import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

interface FormData {
  role: string;
  username: string;
  password: string;
  first_name: string;
  last_name: string;
  nic: string;
  gender: string;
  date_of_birth: string;
  branch_id: string;
  contact_id: string;
}

interface LoginData {
  username: string;
  password: string;
}

interface LoginRegisterProps {
  onLoginSuccess: () => void;
}

interface FormErrors {
  [key: string]: string;
}

const LoginRegister: React.FC<LoginRegisterProps> = ({ onLoginSuccess }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState<FormErrors>({});
  const [generatedEmployeeId, setGeneratedEmployeeId] = useState<string>('');
  const [formData, setFormData] = useState<FormData>({
    role: 'Agent',
    username: '',
    password: '',
    first_name: '',
    last_name: '',
    nic: '',
    gender: 'Male',
    date_of_birth: '',
    branch_id: '',
    contact_id: ''
  });

  const [loginData, setLoginData] = useState<LoginData>({
    username: '',
    password: ''
  });

  const navigate = useNavigate();

  // Validation functions
  const validateLoginForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!loginData.username.trim()) {
      newErrors.loginUsername = 'Username is required';
    }

    if (!loginData.password) {
      newErrors.loginPassword = 'Password is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateRegisterForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.username.trim()) {
      newErrors.username = 'Username is required';
    } else if (formData.username.length < 3) {
      newErrors.username = 'Username must be at least 3 characters';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (!formData.first_name.trim()) {
      newErrors.first_name = 'First name is required';
    }

    if (!formData.last_name.trim()) {
      newErrors.last_name = 'Last name is required';
    }

    if (!formData.nic.trim()) {
      newErrors.nic = 'NIC is required';
    }

    if (!formData.branch_id.trim()) {
      newErrors.branch_id = 'Branch ID is required';
    }

    if (!formData.contact_id.trim()) {
      newErrors.contact_id = 'Contact ID is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!validateRegisterForm()) return;

    setIsLoading(true);
    try {
      const response = await axios.post('/api/register', formData);
      setGeneratedEmployeeId(response.data.employee_id);
      alert(`Registration successful! Your Employee ID is: ${response.data.employee_id}`);
      setFormData({
        role: 'Agent',
        username: '',
        password: '',
        first_name: '',
        last_name: '',
        nic: '',
        gender: 'Male',
        date_of_birth: '',
        branch_id: '',
        contact_id: ''
      });
      setErrors({});
      setIsLogin(true);
    } catch (error: any) {
      alert(error.response?.data?.message || 'Registration failed');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!validateLoginForm()) return;

    setIsLoading(true);
    try {
      const response = await axios.post('/api/login', {
        username: loginData.username,
        password: loginData.password
      });
      const { token, user } = response.data;
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));

      onLoginSuccess();
      navigate('/dashboard');
    } catch (error: any) {
      alert(error.response?.data?.message || 'Login failed');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  const handleLoginChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setLoginData({
      ...loginData,
      [name]: value
    });
    // Clear error when user starts typing
    const errorKey = `login${name.charAt(0).toUpperCase() + name.slice(1)}`;
    if (errors[errorKey]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[errorKey];
        return newErrors;
      });
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Microbanking System</h1>
        <div className="auth-container">
          {/* Loading Overlay */}
          {isLoading && (
            <div className="loading-overlay">
              <div className="loading-spinner">Loading...</div>
            </div>
          )}

          <div className="tabs">
            <button 
              className={isLogin ? 'active' : ''} 
              onClick={() => setIsLogin(true)}
            >
              Login
            </button>
            <button 
              className={!isLogin ? 'active' : ''} 
              onClick={() => setIsLogin(false)}
            >
              Register
            </button>
          </div>

          {isLogin ? (
            <form className="auth-form" onSubmit={handleLogin}>
              <h2>Login</h2>
              <div className="form-group">
                <label>Username:</label>
                <input
                  type="text"
                  name="username"
                  value={loginData.username}
                  onChange={handleLoginChange}
                  required
                  className={errors.loginUsername ? 'error' : ''}
                />
                {errors.loginUsername && <span className="error-text">{errors.loginUsername}</span>}
              </div>
              <div className="form-group">
                <label>Password:</label>
                <input
                  type="password"
                  name="password"
                  value={loginData.password}
                  onChange={handleLoginChange}
                  required
                  className={errors.loginPassword ? 'error' : ''}
                />
                {errors.loginPassword && <span className="error-text">{errors.loginPassword}</span>}
              </div>
              <button type="submit" disabled={isLoading}>
                {isLoading ? 'Logging in...' : 'Login'}
              </button>
            </form>
          ) : (
            <form className="auth-form" onSubmit={handleRegister}>
              <h2>Employee Registration</h2>

              {/* Display generated employee ID after registration */}
              {generatedEmployeeId && (
                <div className="success-message">
                  Your Employee ID: <strong>{generatedEmployeeId}</strong>
                </div>
              )}

              <div className="form-group">
                <label>Role:</label>
                <select 
                  name="role" 
                  value={formData.role} 
                  onChange={handleInputChange}
                  required
                >
                  <option value="Agent">Agent</option>
                  <option value="Manager">Manager</option>
                </select>
              </div>
              <div className="form-group">
                <label>Username:</label>
                <input
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  required
                  className={errors.username ? 'error' : ''}
                />
                {errors.username && <span className="error-text">{errors.username}</span>}
              </div>
              <div className="form-group">
                <label>Password:</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  required
                  className={errors.password ? 'error' : ''}
                />
                {errors.password && <span className="error-text">{errors.password}</span>}
              </div>
              <div className="form-group">
                <label>First Name:</label>
                <input
                  type="text"
                  name="first_name"
                  value={formData.first_name}
                  onChange={handleInputChange}
                  required
                  className={errors.first_name ? 'error' : ''}
                />
                {errors.first_name && <span className="error-text">{errors.first_name}</span>}
              </div>
              <div className="form-group">
                <label>Last Name:</label>
                <input
                  type="text"
                  name="last_name"
                  value={formData.last_name}
                  onChange={handleInputChange}
                  required
                  className={errors.last_name ? 'error' : ''}
                />
                {errors.last_name && <span className="error-text">{errors.last_name}</span>}
              </div>
              <div className="form-group">
                <label>NIC:</label>
                <input
                  type="text"
                  name="nic"
                  value={formData.nic}
                  onChange={handleInputChange}
                  required
                  className={errors.nic ? 'error' : ''}
                />
                {errors.nic && <span className="error-text">{errors.nic}</span>}
              </div>
              <div className="form-group">
                <label>Gender:</label>
                <select 
                  name="gender" 
                  value={formData.gender} 
                  onChange={handleInputChange}
                  required
                >
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="form-group">
                <label>Date of Birth:</label>
                <input
                  type="date"
                  name="date_of_birth"
                  value={formData.date_of_birth}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Branch ID:</label>
                <input
                  type="text"
                  name="branch_id"
                  value={formData.branch_id}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter branch ID (e.g., BR001)"
                  className={errors.branch_id ? 'error' : ''}
                />
                {errors.branch_id && <span className="error-text">{errors.branch_id}</span>}
              </div>
              <div className="form-group">
                <label>Contact ID:</label>
                <input
                  type="text"
                  name="contact_id"
                  value={formData.contact_id}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter contact ID (e.g., CT001)"
                  className={errors.contact_id ? 'error' : ''}
                />
                {errors.contact_id && <span className="error-text">{errors.contact_id}</span>}
              </div>
              <button type="submit" disabled={isLoading}>
                {isLoading ? 'Registering...' : 'Register'}
              </button>
            </form>
          )}
        </div>
      </header>
    </div>
  );
};

export default LoginRegister;
