import React, { useEffect } from 'react'; // Add useEffect import
import { useNavigate } from 'react-router-dom';
import AdminDashboard from './AdminDashboard';
import ManagerDashboard from './ManagerDashboard';
import AgentDashboard from './AgentDashboard';

interface DashboardProps {
  onLogout: () => void;
}

interface User {
  id: string;
  username: string;
  first_name: string;
  secondname: string;
  role: string;
}

const Dashboard: React.FC<DashboardProps> = ({ onLogout }) => {
  const navigate = useNavigate();

  const userData = localStorage.getItem('user');
  const user: User = userData ? JSON.parse(userData) : { 
    id: '', 
    username: '', 
    name: '', 
    role: '' 
  };

  // Add token expiration check
  useEffect(() => {
    const checkToken = () => {
      const token = localStorage.getItem('token');
      if (!token) {
        handleLogout();
      }
    };

    // Check token on component mount
    checkToken();

    // Optional: Set up periodic checks (every 5 minutes)
    const interval = setInterval(checkToken, 5 * 60 * 1000);

    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout();
    navigate('/');
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Microbanking System Dashboard</h1>
        <div className="user-info">
          <span>Welcome, {user.firstname} ({user.role})</span>
          <span className="username">@{user.username}</span>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </header>

      <main className="dashboard-content">
        {user.role === 'Admin' && <AdminDashboard />}
        {user.role === 'Manager' && <ManagerDashboard />}
        {user.role === 'Agent' && <AgentDashboard />}

        {!['Admin', 'Manager', 'Agent'].includes(user.role) && (
          <div>No dashboard available for your role: {user.role}</div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
